#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\develop\project\aibenchmark\node_modules\.pnpm\vite-node@3.2.3_@types+node_c571e257d85f23a4f236af2ebdd80730\node_modules\vite-node\node_modules;D:\develop\project\aibenchmark\node_modules\.pnpm\vite-node@3.2.3_@types+node_c571e257d85f23a4f236af2ebdd80730\node_modules;D:\develop\project\aibenchmark\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/vite-node@3.2.3_@types+node_c571e257d85f23a4f236af2ebdd80730/node_modules/vite-node/node_modules:/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/vite-node@3.2.3_@types+node_c571e257d85f23a4f236af2ebdd80730/node_modules:/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  } else {
    & "node$exe"  "$basedir/../vite-node/vite-node.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
