  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/nuxt@3.17.5_@parcel+watcher_eebb4fcc7e12097465ff64b1ab3e1c65/node_modules/nuxt/bin/node_modules:/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/nuxt@3.17.5_@parcel+watcher_eebb4fcc7e12097465ff64b1ab3e1c65/node_modules/nuxt/node_modules:/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/nuxt@3.17.5_@parcel+watcher_eebb4fcc7e12097465ff64b1ab3e1c65/node_modules:/mnt/d/develop/project/aibenchmark/node_modules/.pnpm/node_modules"
